<!-- Razor view for admin interface -->\n   @model List<FormModel>\n\n   <form method="post" asp-action="InsertForms">\n       @foreach (var form in Model)\n       {\n           <div>\n               <input type="checkbox" name="insertForms" value="@form.Id" />\n               <label>@form.Initials @form.Date @form.Ticket</label>\n           </div>\n       }\n       <button type="submit">Insert Selected Forms</button>\n   </form>