using System;\nusing System.IO;\n\npublic class FileDeleter\n{\n    public static void DeleteReadOnlyFile(string filePath)\n    {\n        FileInfo fileInfo = new FileInfo(filePath);\n        \n        if (fileInfo.IsReadOnly)\n        {\n            // Remove the read-only attribute\n            fileInfo.IsReadOnly = false;\n        }\n        \n        // Now delete the file\n        fileInfo.Delete();\n        \n        Console.WriteLine($"Deleted: {filePath}");\n    }\n\n    public static void DeleteFilesOlderThan(string directoryPath, int days)\n    {\n        DirectoryInfo directory = new DirectoryInfo(directoryPath);\n        DateTime thresholdDate = DateTime.Now.AddDays(-days);\n\n        foreach (FileInfo file in directory.GetFiles("*", SearchOption.AllDirectories))\n        {\n            if (file.LastWriteTime < thresholdDate)\n            {\n                DeleteReadOnlyFile(file.FullName);\n            }\n        }\n    }\n\n    public static void Main()\n    {\n        string junkDirectory = @"C:\path\to\your\junk\directory";\n        int days = 30; // Specify the number of days\n\n        DeleteFilesOlderThan(junkDirectory, days);\n    }\n}