using System;\nusing System.ServiceModel;\n\npublic class DynamicProxyExample\n{\n    public static void Main()\n    {\n        string endpointAddress = "http://localhost:8080/MyService";\n\n        BasicHttpBinding binding = new BasicHttpBinding();\n        EndpointAddress address = new EndpointAddress(endpointAddress);\n\n        ChannelFactory<IMyService> factory = new ChannelFactory<IMyService>(binding, address);\n        IMyService proxy = factory.CreateChannel();\n\n        try\n        {\n            string result = proxy.GetData(42);\n            Console.WriteLine("Result from service: " + result);\n\n            ((IClientChannel)proxy).Close();\n        }\n        catch (CommunicationException e)\n        {\n            Console.WriteLine("Communication error: " + e.Message);\n            ((IClientChannel)proxy).Abort();\n        }\n        catch (TimeoutException e)\n        {\n            Console.WriteLine("Timeout error: " + e.Message);\n            ((IClientChannel)proxy).Abort();\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine("Unexpected error: " + e.Message);\n            ((IClientChannel)proxy).Abort();\n        }\n    }\n}