public EmployeeMap()\n{\n    Id(x => x.EmployeeID);\n    Map(x => x.FirstName);\n    Map(x => x.LastName);\n    Map(x => x.City);\n    Map(x => x.HireDate);\n    Map(x => x.Title);\n    HasMany(x => x.Orders)\n        .KeyColumn("EmployeeID")  // Specify the foreign key column name\n        .Inverse()\n        .Cascade.All();\n}