using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing System.Text.Json;\n\npublic class Program\n{\n    private static readonly HttpClient client = new HttpClient();\n\n    public static async Task Main(string[] args)\n    {\n        try\n        {\n            HttpResponseMessage response = await client.GetAsync("https://yourserver.com/api/hello");\n            response.EnsureSuccessStatusCode();\n            string responseBody = await response.Content.ReadAsStringAsync();\n            var data = JsonSerializer.Deserialize<Dictionary<string, string>>(responseBody);\n            Console.WriteLine($"Status: {data["status"]}, Message: {data["message"]}");\n        }\n        catch (HttpRequestException e)\n        {\n            Console.WriteLine($"Request error: {e.Message}");\n        }\n    }\n}