using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Web.UI.WebControls;\n\npublic partial class Wall : System.Web.UI.Page\n{\n    private string connectionString = "your_connection_string_here";\n\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            LoadPosts();\n        }\n    }\n\n    private void LoadPosts()\n    {\n        using (SqlConnection con = new SqlConnection(connectionString))\n        {\n            using (SqlCommand cmd = new SqlCommand("SELECT * FROM Posts ORDER BY PostDate DESC", con))\n            {\n                con.Open();\n                SqlDataReader reader = cmd.ExecuteReader();\n                rptWall.DataSource = reader;\n                rptWall.DataBind();\n            }\n        }\n    }\n\n    protected void EditPost(object sender, EventArgs e)\n    {\n        LinkButton btn = (LinkButton)sender;\n        int postId = Convert.ToInt32(btn.CommandArgument);\n        // Code to handle editing the post\n    }\n\n    protected void DeletePost(object sender, EventArgs e)\n    {\n        LinkButton btn = (LinkButton)sender;\n        int postId = Convert.ToInt32(btn.CommandArgument);\n\n        using (SqlConnection con = new SqlConnection(connectionString))\n        {\n            using (SqlCommand cmd = new SqlCommand("DELETE FROM Posts WHERE PostID = @PostID", con))\n            {\n                cmd.Parameters.AddWithValue("@PostID", postId);\n                con.Open();\n                cmd.ExecuteNonQuery();\n            }\n        }\n\n        LoadPosts();\n    }\n}