using System;\nusing System.Collections.Generic;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string[] array = {"A", "B", "C", "D", "E", "F", "G"};\n        int columns = 4;\n        \n        List<string>[] sortedColumns = SortIntoColumns(array, columns);\n        \n        for (int i = 0; i < sortedColumns.Length; i++)\n        {\n            foreach (var item in sortedColumns[i])\n            {\n                Console.Write(item);\n            }\n            Console.WriteLine();\n        }\n    }\n\n    public static List<string>[] SortIntoColumns(string[] array, int columns)\n    {\n        int rows = (int)Math.Ceiling(array.Length / (double)columns);\n        List<string>[] sortedColumns = new List<string>[columns];\n        \n        for (int i = 0; i < columns; i++)\n        {\n            sortedColumns[i] = new List<string>();\n        }\n\n        for (int i = 0; i < array.Length; i++)\n        {\n            int columnIndex = i % columns;\n            sortedColumns[columnIndex].Add(array[i]);\n        }\n\n        return sortedColumns;\n    }\n}