using Microsoft.AspNetCore.Mvc.RazorPages;\nusing System.Collections.Generic;\nusing System.Xml.Linq;\n\npublic class IndexModel : PageModel\n{\n    public List<XmlDataModel> XmlData { get; set; }\n\n    public void OnGet()\n    {\n        XmlData = new List<XmlDataModel>();\n        XDocument doc = XDocument.Load("https://example.com/data.xml");\n\n        foreach (var element in doc.Descendants("Data"))\n        {\n            XmlData.Add(new XmlDataModel\n            {\n                Column1 = element.Element("Column1")?.Value,\n                Column2 = element.Element("Column2")?.Value\n            });\n        }\n    }\n}\n\npublic class XmlDataModel\n{\n    public string Column1 { get; set; }\n    public string Column2 { get; set; }\n}