using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    // Import the external function from the C++ DLL\n    [DllImport("YourCppDll.dll", CallingConvention = CallingConvention.Cdecl)]\n    private static extern int YourFunction();\n\n    static void Main()\n    {\n        try\n        {\n            int result = YourFunction();\n            Console.WriteLine("Result: " + result);\n        }\n        catch (DllNotFoundException e)\n        {\n            Console.WriteLine("DLL not found: " + e.Message);\n        }\n        catch (BadImageFormatException e)\n        {\n            Console.WriteLine("Platform mismatch: " + e.Message);\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine("Error loading DLL: " + e.Message);\n        }\n    }\n}