protected void LazyLoadUpdatePanel_Tick(object sender, EventArgs e)\n{\n    if (datasource == null)\n    {\n        System.Threading.Thread.Sleep(2000);\n        datasource = customer.Orders;\n\n        // Save current page index\n        int currentPageIndex = OrdersGridView.PageIndex;\n\n        OrdersGridView.DataBind();\n\n        // Restore page index\n        OrdersGridView.PageIndex = currentPageIndex;\n\n        OrdersUpdatePanel.Update();\n    }\n    LazyLoadUpdatePanel.Enabled = false;\n}\n\n// Ensure you handle the PageIndexChanging event to update the GridView's page index\nprotected void OrdersGridView_PageIndexChanging(object sender, GridViewPageEventArgs e)\n{\n    OrdersGridView.PageIndex = e.NewPageIndex;\n    OrdersGridView.DataBind();\n}