using System;\nusing System.Web;\n\npublic class SecureSessionModule : IHttpModule\n{\n    public void Init(HttpApplication application)\n    {\n        application.PostReleaseRequestState += new EventHandler(OnPostReleaseRequestState);\n    }\n\n    private void OnPostReleaseRequestState(object sender, EventArgs e)\n    {\n        HttpApplication application = (HttpApplication)sender;\n        HttpCookie sessionCookie = application.Response.Cookies["ASPSESSIONID"];\n\n        if (sessionCookie != null)\n        {\n            sessionCookie.Secure = true; // Mark the cookie as secure\n            sessionCookie.HttpOnly = true; // Mark the cookie as HttpOnly for additional security\n        }\n    }\n\n    public void Dispose() { }\n}