using System;\nusing System.Threading;\nusing System.Windows.Forms;\n\nclass Program\n{\n    static void Main()\n    {\n        // Start a new thread to show a message box\n        Thread thread = new Thread(ShowMessageBox);\n        thread.Start();\n\n        // Wait for the message box to appear\n        Thread.Sleep(2000); // Adjust the sleep time as necessary\n\n        // Simulate an Enter key press to click the default button on the message box\n        SendKeys.SendWait("{ENTER}");\n    }\n\n    static void ShowMessageBox()\n    {\n        MessageBox.Show("This is a test message box.", "Test", MessageBoxButtons.OKCancel);\n    }\n}