using System;\nusing System.IO;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        string inputFilePath = "path/to/your/ansi_file.txt";\n        string outputFilePath = "path/to/your/ascii_file.txt";\n\n        using (FileStream inputStream = new FileStream(inputFilePath, FileMode.Open, FileAccess.Read))\n        using (BinaryReader reader = new BinaryReader(inputStream, Encoding.Default))\n        using (StreamWriter writer = new StreamWriter(outputFilePath, false, Encoding.ASCII))\n        {\n            while (reader.BaseStream.Position != reader.BaseStream.Length)\n            {\n                string line = reader.ReadString();\n                byte[] ansiBytes = Encoding.Default.GetBytes(line);\n                byte[] asciiBytes = Encoding.Convert(Encoding.Default, Encoding.ASCII, ansiBytes);\n                string asciiLine = Encoding.ASCII.GetString(asciiBytes);\n                writer.WriteLine(asciiLine);\n            }\n        }\n    }\n}