using System.IO;\nusing iTextSharp.text.pdf;\n\npublic void FillPdfForm(string templatePath, string outputPath, Dictionary<string, string> fieldValues)\n{\n    using (PdfReader reader = new PdfReader(templatePath))\n    using (FileStream fs = new FileStream(outputPath, FileMode.Create, FileAccess.Write))\n    using (PdfStamper stamper = new PdfStamper(reader, fs))\n    {\n        AcroFields form = stamper.AcroFields;\n\n        // Set field values\n        foreach (var field in fieldValues)\n        {\n            form.SetField(field.Key, field.Value);\n        }\n\n        // Flatten the form to prevent further editing\n        stamper.FormFlattening = true;\n\n        // Trigger all JavaScript calculations\n        PdfAction action = PdfAction.JavaScript("this.calculateNow();", stamper.Writer);\n        stamper.Writer.AddJavaScript(action);\n\n        stamper.Close();\n    }\n}