using System;\nusing System.IO;\n\npublic class FileJoiner\n{\n    public static void JoinFiles(string[] inputFiles, string outputFile)\n    {\n        using (FileStream outputStream = new FileStream(outputFile, FileMode.Create))\n        {\n            foreach (string inputFile in inputFiles)\n            {\n                using (FileStream inputStream = new FileStream(inputFile, FileMode.Open))\n                {\n                    inputStream.CopyTo(outputStream);\n                }\n            }\n        }\n    }\n\n    public static void Main()\n    {\n        string[] filesToJoin = { "part1.bin", "part2.bin", "part3.bin" }; // List your file parts here\n        string outputFile = "joinedFile.bin";\n\n        JoinFiles(filesToJoin, outputFile);\n        Console.WriteLine("Files have been successfully joined into " + outputFile);\n    }\n}