public class BusinessMap : ClassMap<Business>\n{\n    public BusinessMap()\n    {\n        Table("Businesses");\n        Id(x => x.BusinessId).Column("BusinessId").GeneratedBy.Identity();\n        Map(x => x.Name).Column("BusinessName");\n        Map(x => x.LastModified).Column("LastModifiedOn");\n        HasMany<Review>(x => x.Reviews)\n            .Inverse()\n            .Cascade.AllDeleteOrphan()\n            .LazyLoad();\n    }\n}