// Define a contract for communication\npublic interface IAddInContract : System.AddIn.Contract.IContract\n{\n    void DoWork();\n}\n\n// Define the add-in\n[AddIn("Sample AddIn")]\npublic class SampleAddIn : IAddInContract\n{\n    public void DoWork()\n    {\n        Console.WriteLine("Sample AddIn Work");\n    }\n}\n\n// Host application\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string addInRoot = "path to add-in root";\n        AddInStore.Update(addInRoot);\n        var addInToken = AddInStore.FindAddIns(typeof(IAddInContract), addInRoot)[0];\n        \n        var addIn = addInToken.Activate<IAddInContract>(AddInSecurityLevel.Host);\n        addIn.DoWork();\n    }\n}