public string GetOrCreateUserIdentifier(HttpContextBase context)\n{\n    var userCookie = context.Request.Cookies["UserIdentifier"];\n    if (userCookie == null)\n    {\n        var userId = Guid.NewGuid().ToString();\n        context.Response.Cookies.Add(new HttpCookie("UserIdentifier", userId)\n        {\n            Expires = DateTime.Now.AddMonths(6) // Cookie expiration\n        });\n        return userId;\n    }\n    return userCookie.Value;\n}