using System;\nusing Microsoft.Web.Administration;\n\npublic class IISHostHeaderManager\n{\n    public static void AddHostHeader(string siteName, string hostHeader, string ipAddress, int port)\n    {\n        using (ServerManager serverManager = new ServerManager())\n        {\n            Site site = serverManager.Sites[siteName];\n            if (site == null)\n            {\n                throw new InvalidOperationException($"Site '{siteName}' not found.");\n            }\n\n            Binding binding = site.Bindings.CreateElement();\n            binding.Protocol = "http";\n            binding.BindingInformation = $"{ipAddress}:{port}:{hostHeader}";\n\n            site.Bindings.Add(binding);\n            serverManager.CommitChanges();\n        }\n    }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        try\n        {\n            string siteName = "YourWebsiteName";\n            string hostHeader = "yourhostheader.com";\n            string ipAddress = "*"; // Use "*" for all unassigned IPs or specify an IP address\n            int port = 80; // Specify the port number\n\n            IISHostHeaderManager.AddHostHeader(siteName, hostHeader, ipAddress, port);\n            Console.WriteLine("Host header added successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}