using System;\n\n[Flags]\npublic enum CheckType\n{\n    None = 0,\n    Form = 1,\n    QueryString = 2,\n    TempData = 4,\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        CheckType theCheckType = CheckType.Form | CheckType.TempData;\n\n        foreach (CheckType check in Enum.GetValues(typeof(CheckType)))\n        {\n            if (check != CheckType.None && theCheckType.HasFlag(check))\n            {\n                switch (check)\n                {\n                    case CheckType.Form:\n                        DoSomething();\n                        break;\n                    case CheckType.QueryString:\n                        DoSomethingElse();\n                        break;\n                    case CheckType.TempData:\n                        DoWhatever();\n                        break;\n                }\n            }\n        }\n    }\n\n    public static void DoSomething()\n    {\n        Console.WriteLine("Doing Something");\n    }\n\n    public static void DoSomethingElse()\n    {\n        Console.WriteLine("Doing Something Else");\n    }\n\n    public static void DoWhatever()\n    {\n        Console.WriteLine("Doing Whatever");\n    }\n}