using NUnit.Framework;\nusing OpenQA.Selenium;\nusing OpenQA.Selenium.Remote;\nusing System;\n\n[assembly: Parallelizable(ParallelScope.Fixtures)]\n[assembly: LevelOfParallelism(4)]\n\nnamespace SeleniumTests\n{\n    [TestFixture]\n    [Parallelizable(ParallelScope.All)]\n    public class SeleniumGridTests\n    {\n        private IWebDriver driver;\n        private string gridUrl = "http://localhost:4444/wd/hub"; // Replace with your Selenium Grid URL\n\n        [SetUp]\n        public void SetUp()\n        {\n            var capabilities = new DesiredCapabilities();\n            capabilities.SetCapability("browserName", "chrome"); // Change as needed\n            driver = new RemoteWebDriver(new Uri(gridUrl), capabilities);\n        }\n\n        [Test]\n        public void TestExample1()\n        {\n            driver.Navigate().GoToUrl("https://www.example.com");\n            Assert.IsTrue(driver.Title.Contains("Example"));\n        }\n\n        [Test]\n        public void TestExample2()\n        {\n            driver.Navigate().GoToUrl("https://www.example.com");\n            Assert.IsTrue(driver.Title.Contains("Example"));\n        }\n\n        [TearDown]\n        public void TearDown()\n        {\n            driver.Quit();\n        }\n    }\n}