using System;\nusing System.IO;\n\npublic class MoneyWriter\n{\n    public static void WriteMoneyToFile(string filePath, decimal moneyValue)\n    {\n        // Convert the decimal money value to the corresponding 8-byte signed integer\n        long moneyAsLong = (long)(moneyValue * 10000);\n\n        // Write the value to a file using a BinaryWriter\n        using (FileStream fs = new FileStream(filePath, FileMode.Create, FileAccess.Write))\n        using (BinaryWriter writer = new BinaryWriter(fs))\n        {\n            writer.Write(moneyAsLong);\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        // Example usage\n        string filePath = "moneyData.dat";\n        decimal moneyValue = 1234.5678m;\n\n        WriteMoneyToFile(filePath, moneyValue);\n\n        Console.WriteLine("Money value written to file successfully.");\n    }\n}