protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        CreateDynamicControls();\n    }\n}\n\nprivate void CreateDynamicControls()\n{\n    // Create a new panel to hold the controls\n    Panel panel = new Panel();\n\n    // Create a new TextBox and set its ID\n    TextBox textBox = new TextBox();\n    textBox.ID = "DynamicTextBox";\n\n    // Add the TextBox to the panel\n    panel.Controls.Add(textBox);\n\n    // Create a RequiredFieldValidator and set its properties\n    RequiredFieldValidator requiredValidator = new RequiredFieldValidator();\n    requiredValidator.ID = "RequiredValidator";\n    requiredValidator.ControlToValidate = textBox.ID;\n    requiredValidator.ErrorMessage = "This field is required.";\n    requiredValidator.Display = ValidatorDisplay.Dynamic;\n    \n    // Add the RequiredFieldValidator to the panel\n    panel.Controls.Add(requiredValidator);\n\n    // Create a RegularExpressionValidator and set its properties\n    RegularExpressionValidator regexValidator = new RegularExpressionValidator();\n    regexValidator.ID = "RegexValidator";\n    regexValidator.ControlToValidate = textBox.ID;\n    regexValidator.ValidationExpression = @"^\d+$"; // Example regex for digits only\n    regexValidator.ErrorMessage = "Please enter only digits.";\n    regexValidator.Display = ValidatorDisplay.Dynamic;\n\n    // Add the RegularExpressionValidator to the panel\n    panel.Controls.Add(regexValidator);\n\n    // Create a TableCell and add the panel to it\n    TableCell tableCell = new TableCell();\n    tableCell.Controls.Add(panel);\n\n    // Create a TableRow and add the TableCell to it\n    TableRow tableRow = new TableRow();\n    tableRow.Cells.Add(tableCell);\n\n    // Add the TableRow to a Table (assuming you have a Table control with ID 'DynamicTable' on your page)\n    DynamicTable.Rows.Add(tableRow);\n}\n\nprotected override void OnPreRender(EventArgs e)\n{\n    base.OnPreRender(e);\n    // Ensure the controls are created on every postback\n    if (IsPostBack)\n    {\n        CreateDynamicControls();\n    }\n}