using System;\nusing System.IO;\nusing Microsoft.Deployment.WindowsInstaller;\n\npublic class CustomActions\n{\n    [CustomAction]\n    public static ActionResult CheckPrinterDrivers(Session session)\n    {\n        string driverPath = @"C:\Path\To\PrinterDriver"; // Example path\n        bool driversInstalled = Directory.Exists(driverPath);\n\n        session["PRINTER_DRIVERS_INSTALLED"] = driversInstalled ? "1" : "0";\n        return ActionResult.Success;\n    }\n}