using System;\nusing System.IO;\nusing Xceed.Words.NET;\nusing PdfSharp.Pdf;\nusing PdfSharp.Drawing;\n\nclass Program\n{\n    static void Main()\n    {\n        string wordFilePath = @"path\to\your\file.docx";\n        string pdfFilePath = @"path\to\output\file.pdf";\n\n        ConvertWordToPdf(wordFilePath, pdfFilePath);\n    }\n\n    static void ConvertWordToPdf(string wordFilePath, string pdfFilePath)\n    {\n        // Load the Word document\n        using (DocX document = DocX.Load(wordFilePath))\n        {\n            // Create a new PDF document\n            PdfDocument pdf = new PdfDocument();\n            pdf.Info.Title = "Converted from Word";\n\n            // Create a new PDF page\n            PdfPage pdfPage = pdf.AddPage();\n            XGraphics gfx = XGraphics.FromPdfPage(pdfPage);\n\n            // Convert each paragraph to text\n            int yPoint = 0;\n            foreach (var paragraph in document.Paragraphs)\n            {\n                gfx.DrawString(paragraph.Text, new XFont("Verdana", 12), XBrushes.Black,\n                    new XRect(0, yPoint, pdfPage.Width.Point, pdfPage.Height.Point),\n                    XStringFormats.TopLeft);\n                yPoint += 20; // Adjust the line height as needed\n            }\n\n            // Save the PDF document\n            pdf.Save(pdfFilePath);\n        }\n    }\n}