using Atalasoft.Twain;\n\npublic class ScannerService\n{\n    private TwainSession _twainSession;\n    private TwainSource _twainSource;\n\n    public void Initialize()\n    {\n        _twainSession = new TwainSession();\n        _twainSession.Open();\n        _twainSource = _twainSession.FirstSource(); // Assuming the scanner is the first source\n        _twainSource.TransferImage += TwainSource_TransferImage;\n        _twainSource.ScanningComplete += TwainSource_ScanningComplete;\n    }\n\n    private void TwainSource_TransferImage(object sender, TransferImageEventArgs e)\n    {\n        // Process the scanned image\n        var acquiredImage = e.Image;\n        // Process the acquiredImage as needed\n    }\n\n    private void TwainSource_ScanningComplete(object sender, EventArgs e)\n    {\n        // Handle the scanning completion event\n    }\n\n    public void StartScanning()\n    {\n        _twainSource.Acquire();\n    }\n}