using System;\nusing System.Text.RegularExpressions;\nusing System.Web;\n\npublic class QueryStringValidator\n{\n    public static bool IsValidQueryString(string queryString, string pattern)\n    {\n        if (string.IsNullOrEmpty(queryString))\n        {\n            return false;\n        }\n\n        Regex regex = new Regex(pattern);\n        return regex.IsMatch(queryString);\n    }\n\n    public static void ValidateQueryStrings(HttpRequest request)\n    {\n        string stringPattern = @"^[a-zA-Z]+$"; // Example pattern for strings only\n        string numericPattern = @"^\d+$"; // Example pattern for numeric only\n\n        string queryStringValue = request.QueryString["yourQueryStringKey"];\n\n        if (!IsValidQueryString(queryStringValue, stringPattern))\n        {\n            // Handle invalid query string (e.g., log, throw error, etc.)\n            throw new ArgumentException("Invalid query string value.");\n        }\n\n        // Add more validations as needed\n    }\n}