[TestClass]\npublic class HomeControllerTests\n{\n    [TestMethod]\n    public void Index_Returns_View()\n    {\n        // Arrange\n        var mockFooService = new Mock<IFooService>();\n        mockFooService.Setup(service => service.GetFoo()).Returns("Mock Foo Data");\n\n        var controller = new HomeController(mockFooService.Object);\n\n        // Act\n        var result = controller.Index() as ViewResult;\n\n        // Assert\n        Assert.IsNotNull(result);\n        Assert.AreEqual("Mock Foo Data", controller.ViewData["foo"]);\n    }\n}