using System;\nusing IronPython.Hosting;\nusing Microsoft.Scripting.Hosting;\n\npublic class Program\n{\n    public static void Main(string[] args)\n    {\n        // Create a new Python runtime engine\n        ScriptEngine engine = Python.CreateEngine();\n        \n        // Create a scope for the Python engine\n        ScriptScope scope = engine.CreateScope();\n        \n        // Create an instance of the API class\n        MyApi api = new MyApi();\n        \n        // Expose the API instance to the Python script\n        scope.SetVariable("api", api);\n        \n        // A simple Python script that uses the API\n        string script = @"\nimport clr\nclr.AddReference('System')\nfrom System import Console\n\nresult = api.Hello('World')\nConsole.WriteLine(result)\n";\n        \n        // Execute the script\n        ScriptSource source = engine.CreateScriptSourceFromString(script);\n        source.Execute(scope);\n    }\n}