protected void ExecuteWithPermissionCheck(Permission permission, Action action, object entity)\n   {\n       var user = // Get current user\n\n       if (new PermissionAttribute(permission).IsAuthorized(user, entity))\n       {\n           action();\n       }\n       else\n       {\n           // Handle unauthorized access\n           Response.Redirect("Unauthorized.aspx");\n       }\n   }