public static class GenericHelper\n{\n    public static bool IsNullOrDefault<T>(T value)\n    {\n        if (value == null)\n        {\n            return true;\n        }\n\n        if (EqualityComparer<T>.Default.Equals(value, default(T)))\n        {\n            return true;\n        }\n\n        return false;\n    }\n}\n\n// Usage\nif (GenericHelper.IsNullOrDefault(thing))\n{\n    // Do something\n}\nelse\n{\n    // Do something else\n}