using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\npublic class Program\n{\n    public static async Task Main(string[] args)\n    {\n        Console.WriteLine("Starting request...");\n        \n        // Call the long-running web request asynchronously\n        string result = await MakeWebRequestAsync("https://example.com/long-running-request");\n\n        // Continue processing after the request completes\n        Console.WriteLine("Request completed.");\n        Console.WriteLine("Result: " + result);\n    }\n\n    public static async Task<string> MakeWebRequestAsync(string url)\n    {\n        using (HttpClient client = new HttpClient())\n        {\n            // Set a timeout for the request\n            client.Timeout = TimeSpan.FromSeconds(120);\n\n            try\n            {\n                HttpResponseMessage response = await client.GetAsync(url);\n                response.EnsureSuccessStatusCode();\n                string responseBody = await response.Content.ReadAsStringAsync();\n                return responseBody;\n            }\n            catch (HttpRequestException e)\n            {\n                Console.WriteLine("Request error: " + e.Message);\n                return null;\n            }\n        }\n    }\n}