[AttributeUsage(AttributeTargets.Method, Inherited = true, AllowMultiple = true)]\n   public class PermissionAttribute : Attribute\n   {\n       public Permission Permission { get; private set; }\n\n       public PermissionAttribute(Permission permission)\n       {\n           this.Permission = permission;\n       }\n\n       public bool IsAuthorized(User user, object entity)\n       {\n           // Implement your logic to check if the user has the specified permission for the entity\n           // For example:\n           return user.HasPermission(this.Permission, entity);\n       }\n   }