// Example of caching user data\npublic MembershipUser GetCachedUser(string username)\n{\n    string cacheKey = "User_" + username;\n    var cachedUser = HttpContext.Current.Cache[cacheKey] as MembershipUser;\n\n    if (cachedUser == null)\n    {\n        cachedUser = Membership.GetUser(username);\n        if (cachedUser != null)\n        {\n            HttpContext.Current.Cache.Insert(cacheKey, cachedUser, null, DateTime.Now.AddMinutes(10), Cache.NoSlidingExpiration);\n        }\n    }\n\n    return cachedUser;\n}