protected Label FindLabel(Control parent, string id)\n{\n    if (parent == null) return null;\n    \n    Label label = parent.FindControl(id) as Label;\n    if (label != null) return label;\n\n    foreach (Control child in parent.Controls)\n    {\n        label = FindLabel(child, id);\n        if (label != null) return label;\n    }\n\n    return null;\n}\n\nprotected void Page_Load(object sender, EventArgs e)\n{\n    Label label1 = FindLabel(this.Parent, "label1");\n    if (label1 != null)\n    {\n        // Do something with the label\n        label1.Text = "Label found!";\n    }\n    else\n    {\n        // Handle the case where the label is not found\n        // Example: throw an exception or log an error\n    }\n}