using (SqlConnection connection = new SqlConnection(connectionString))\n{\n    SqlCommand cmd = new SqlCommand("UpdatePaid", connection);\n    cmd.CommandType = CommandType.StoredProcedure;\n\n    cmd.Parameters.Add(new SqlParameter("v_iid", SqlDbType.Float)).SourceColumn = "I";\n    cmd.Parameters.Add(new SqlParameter("v_pd", SqlDbType.Bit)).SourceColumn = "Paid";\n    cmd.Parameters.Add(new SqlParameter("v_Projected", SqlDbType.Money)).SourceColumn = "ProjectedCost";\n\n    connection.Open();\n    cmd.Prepare();\n\n    // Execute the command or assign it to a data adapter's command as needed\n}