using System;\nusing System.IO;\nusing System.Web;\n\npublic partial class _Default : System.Web.UI.Page\n{\n    protected void UploadButton_Click(object sender, EventArgs e)\n    {\n        if (FileUpload1.HasFile)\n        {\n            try\n            {\n                // Define the folder path\n                string folderPath = Server.MapPath("~/pics/");\n\n                // Check if the folder exists; if not, create it\n                if (!Directory.Exists(folderPath))\n                {\n                    Directory.CreateDirectory(folderPath);\n                }\n\n                // Get the file name and save it to the folder\n                string fileName = Path.GetFileName(FileUpload1.FileName);\n                FileUpload1.SaveAs(Path.Combine(folderPath, fileName));\n\n                // Provide a success message\n                StatusLabel.Text = "Upload status: File uploaded!";\n            }\n            catch (Exception ex)\n            {\n                // Provide an error message\n                StatusLabel.Text = "Upload status: The file could not be uploaded. The following error occurred: " + ex.Message;\n            }\n        }\n        else\n        {\n            StatusLabel.Text = "Upload status: No file selected!";\n        }\n    }\n}