using System;\nusing System.Numerics;\n\nclass Program\n{\n    static void Main()\n    {\n        string strNumber = "010";\n        int numberToAdd = 1;\n\n        // Convert the string to BigInteger\n        BigInteger bigInt = BigInteger.Parse(strNumber);\n\n        // Perform the addition\n        BigInteger result = bigInt + numberToAdd;\n\n        // Convert the result back to string and preserve leading zeros\n        string resultStr = result.ToString("D3"); // "D3" ensures at least 3 digits are shown\n\n        Console.WriteLine(resultStr); // Output: 011\n    }\n}