using System;\nusing System.Collections.Generic;\nusing System.IO;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string tnsFilePath = "path_to_your_tnsnames.ora";\n        \n        // Read the content of the tnsnames.ora file\n        string tnsFileContent = File.ReadAllText(tnsFilePath);\n\n        // Regular expression to match the TNS names\n        Regex regex = new Regex(@"^(\w+)\s*=", RegexOptions.Multiline);\n\n        // Find matches\n        MatchCollection matches = regex.Matches(tnsFileContent);\n\n        // Extract and display TNS names\n        List<string> tnsNames = new List<string>();\n\n        foreach (Match match in matches)\n        {\n            tnsNames.Add(match.Groups[1].Value);\n        }\n\n        // Display the TNS names\n        Console.WriteLine("TNS Names found:");\n        foreach (string tnsName in tnsNames)\n        {\n            Console.WriteLine(tnsName);\n        }\n    }\n}