using System;\nusing System.IO;\nusing System.Windows.Forms;\n\npublic class RichTextBoxExample\n{\n    public static void SavePlainText(RichTextBox richTextBox, string filePath)\n    {\n        try\n        {\n            // Extract plain text from the RichTextBox\n            string plainText = richTextBox.Text;\n\n            // Write the plain text to a file\n            File.WriteAllText(filePath, plainText);\n        }\n        catch (Exception ex)\n        {\n            MessageBox.Show("An error occurred while saving the file: " + ex.Message);\n        }\n    }\n}