using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        // Example with ArrayList (not recommended for new code)\n        ArrayList arrayList = new ArrayList() { 1, 2, 3, 4, 5 };\n        string result = String.Join(",", arrayList.ToArray());\n        Console.WriteLine(result); // Output: 1,2,3,4,5\n\n        // Recommended approach with List<T>\n        List<int> list = new List<int>() { 1, 2, 3, 4, 5 };\n        string resultList = String.Join(",", list);\n        Console.WriteLine(resultList); // Output: 1,2,3,4,5\n    }\n}