using System;\nusing System.Security.Permissions;\nusing System.Web;\n\npublic partial class _Default : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        AspNetHostingPermission permission = new AspNetHostingPermission(PermissionState.Unrestricted);\n        try\n        {\n            permission.Demand();\n            Response.Write("The application is running in full trust.");\n        }\n        catch (SecurityException)\n        {\n            Response.Write("The application is not running in full trust.");\n        }\n    }\n}