using System;\nusing System.Net.Sockets;\nusing System.Text;\n\npublic class SmtpValidator\n{\n    public static bool ValidateEmail(string email)\n    {\n        try\n        {\n            // Extract domain from email address\n            string domain = email.Split('@')[1];\n            string server = $"mx.{domain}";\n\n            // Connect to the SMTP server\n            using (TcpClient client = new TcpClient(server, 25))\n            using (NetworkStream stream = client.GetStream())\n            using (System.IO.StreamReader reader = new System.IO.StreamReader(stream))\n            using (System.IO.StreamWriter writer = new System.IO.StreamWriter(stream))\n            {\n                writer.AutoFlush = true;\n\n                // Read server response\n                string response = reader.ReadLine();\n                if (!response.StartsWith("220"))\n                {\n                    return false;\n                }\n\n                // Send HELO command\n                writer.WriteLine($"HELO {domain}");\n                response = reader.ReadLine();\n                if (!response.StartsWith("250"))\n                {\n                    return false;\n                }\n\n                // Send MAIL FROM command\n                writer.WriteLine($"MAIL FROM:<test@{domain}>");\n                response = reader.ReadLine();\n                if (!response.StartsWith("250"))\n                {\n                    return false;\n                }\n\n                // Send RCPT TO command\n                writer.WriteLine($"RCPT TO:<{email}>");\n                response = reader.ReadLine();\n\n                // Check if the email is valid\n                if (response.StartsWith("250"))\n                {\n                    // Send QUIT command\n                    writer.WriteLine("QUIT");\n                    return true;\n                }\n                else\n                {\n                    // Send QUIT command\n                    writer.WriteLine("QUIT");\n                    return false;\n                }\n            }\n        }\n        catch (Exception)\n        {\n            return false;\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        string testEmail = "example@example.com";\n        bool isValid = ValidateEmail(testEmail);\n        Console.WriteLine($"Email '{testEmail}' is valid: {isValid}");\n    }\n}