using CommandLine;\nusing System;\n\nclass Program\n{\n    public class Options\n    {\n        [Option('t', "target", Required = true, HelpText = "Set the target.")]\n        public string Target { get; set; }\n\n        [Option('v', "verbose", Default = false, HelpText = "Set output to verbose messages.")]\n        public bool Verbose { get; set; }\n    }\n\n    static void Main(string[] args)\n    {\n        Parser.Default.ParseArguments<Options>(args)\n              .WithParsed<Options>(opts => RunOptionsAndReturnExitCode(opts))\n              .WithNotParsed<Options>((errs) => HandleParseError(errs));\n    }\n\n    static void RunOptionsAndReturnExitCode(Options opts)\n    {\n        if (opts.Verbose)\n        {\n            Console.WriteLine($"Target is set to: {opts.Target}");\n            Console.WriteLine("Verbose output enabled.");\n        }\n        else\n        {\n            Console.WriteLine($"Target is set to: {opts.Target}");\n        }\n    }\n\n    static void HandleParseError(IEnumerable<Error> errs)\n    {\n        // Handle errors here\n    }\n}