using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic class DataInserter\n{\n    private readonly string _connectionString;\n\n    public DataInserter(string connectionString)\n    {\n        _connectionString = connectionString;\n    }\n\n    public void InsertOrder(int orderId, string otherData)\n    {\n        using (SqlConnection connection = new SqlConnection(_connectionString))\n        {\n            connection.Open();\n            using (SqlTransaction transaction = connection.BeginTransaction())\n            {\n                try\n                {\n                    using (SqlCommand command = connection.CreateCommand())\n                    {\n                        command.Transaction = transaction;\n\n                        // Enable IDENTITY_INSERT\n                        command.CommandText = "SET IDENTITY_INSERT orders ON";\n                        command.ExecuteNonQuery();\n\n                        // Insert data\n                        command.CommandText = "INSERT INTO orders (order_Id, other_column) VALUES (@orderId, @otherData)";\n                        command.Parameters.AddWithValue("@orderId", orderId);\n                        command.Parameters.AddWithValue("@otherData", otherData);\n                        command.ExecuteNonQuery();\n\n                        // Disable IDENTITY_INSERT\n                        command.CommandText = "SET IDENTITY_INSERT orders OFF";\n                        command.ExecuteNonQuery();\n                    }\n\n                    transaction.Commit();\n                }\n                catch (Exception ex)\n                {\n                    transaction.Rollback();\n                    Console.WriteLine("Exception when attempting to insert a value into the orders table: " + ex.Message);\n                }\n            }\n        }\n    }\n}