string buildingFile = Server.MapPath("buildings.txt");\nStreamReader fileStreamReader = File.OpenText(buildingFile);\n\nwhile (fileStreamReader.Peek() != -1)\n{\n    string line = fileStreamReader.ReadLine();\n    string[] words = line.Split(',');\n\n    if (words[0].Trim() == "bldgA")\n    {\n        Console.WriteLine($"You are in {words[0].Trim()} and your name is {words[1].Trim()} and your duty is {words[2].Trim()}");\n    }\n    else if (words[0].Trim() == "bldgB")\n    {\n        Console.WriteLine($"You are in {words[0].Trim()} and your name is {words[1].Trim()} and your duty is {words[2].Trim()}");\n    }\n}\n\nfileStreamReader.Close();