using System;\nusing System.Threading;\n\nclass ServiceApp\n{\n    private static Mutex mutex;\n\n    static void Main()\n    {\n        bool isCreatedNew;\n        mutex = new Mutex(true, "Global\\ServiceAppMutex", out isCreatedNew);\n\n        if (!isCreatedNew)\n        {\n            Console.WriteLine("Service application is already running.");\n            return;\n        }\n\n        Console.WriteLine("Service application started.");\n        // Your service logic here\n\n        Console.ReadLine(); // Keep the application running\n    }\n}