using System;\nusing System.Collections.Specialized;\nusing System.Web;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string link = "?action=a&current=b&something=c";\n        NameValueCollection queryParameters = HttpUtility.ParseQueryString(link.Substring(link.IndexOf("?") + 1));\n\n        string currentValue = queryParameters["current"];\n        Console.WriteLine("Current: " + currentValue);\n\n        // You can access other parameters similarly\n        string actionValue = queryParameters["action"];\n        Console.WriteLine("Action: " + actionValue);\n\n        string somethingValue = queryParameters["something"];\n        Console.WriteLine("Something: " + somethingValue);\n    }\n}