using SubSonic;\n\n// Assuming you have your SubSonic setup and classes generated for Master1, Sub1, and Master1Sub1_Map\n\npublic Sub1Collection GetSub1RecordsForMaster1(int master1Id)\n{\n    Sub1Collection sub1Collection = new Sub1Collection();\n    \n    // Create a query to select all Sub1 records that match the Master1 ID through the mapping table\n    var query = new Query(Sub1.Schema)\n        .INNER_JOIN(Master1Sub1_Map.Schema.TableName, Master1Sub1_Map.Columns.Sub1Id, Sub1.Columns.Id)\n        .WHERE(Master1Sub1_Map.Columns.Master1Id).IsEqualTo(master1Id);\n    \n    // Load the results into the Sub1Collection\n    sub1Collection.LoadAndCloseReader(query.ExecuteReader());\n    \n    return sub1Collection;\n}