using System;\nusing System.ComponentModel.Composition;\nusing System.ComponentModel.Composition.Hosting;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create an AssemblyCatalog for the current assembly\n        var catalog = new AssemblyCatalog(typeof(Program).Assembly);\n        \n        // Create a CompositionContainer with the catalog\n        var container = new CompositionContainer(catalog);\n        \n        // Compose the parts\n        var program = new Program();\n        container.ComposeParts(program);\n        \n        // Use the composed parts\n        program.Run();\n    }\n\n    [Import]\n    public ISomeService SomeService { get; set; }\n\n    public void Run()\n    {\n        SomeService.DoWork();\n    }\n}\n\npublic interface ISomeService\n{\n    void DoWork();\n}\n\n[Export(typeof(ISomeService))]\npublic class SomeService : ISomeService\n{\n    public void DoWork()\n    {\n        Console.WriteLine("Work is being done.");\n    }\n}